interface Data {
  series: {
    name: string;
    values: number[];
  }[];
  common: number | string[];
}

interface Margin {
  left?: number;
  right?: number;
  top?: number;
  bottom?: number;
}

interface Thresholds {
  color: string;
  moreThan?: number;
  lessThan?: number;
}

interface Props {
  data: Data[];
  element: HTMLElement;
  height?: number;
  margin?: Margin;
  thresholds?: Thresholds[];
  y?: {
    startFromZero?: boolean;
    isLogarithmic?: boolean;
    unit?: string;
  }
  x?: {
    isTime?: boolean;
    unit?: string;
  }
}

export declare class LineChart {
  constructor(props: Props);

  init(): void;
  draw(data?: Data): void;
  destroy(): void;
}
